/**
 * DownloadTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class DownloadTable extends Doctrine_Table {

  /**
   * Returns an instance of this class.
   *
   * @return DownloadTable The table instance
   */
  public static function getInstance() {
    return Doctrine_Core::getTable('Download');
  }

  public function queryIncrementalDownloads(Petition $petition, $subscriber = false) {
    return $this->createQuery()
        ->where('petition_id = ?', $petition->getId())
        ->andWhere('subscriber = ?', $subscriber ? 1 : 0)
        ->andWhere('incremental = 1')
        ->orderBy('created_at DESC');
  }

}
