/**
 * MailingListMetaChoiceTable
 *
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class MailingListMetaChoiceTable extends Doctrine_Table {

  /**
   * Returns an instance of this class.
   *
   * @return MailingListMetaChoiceTable
   */
  public static function getInstance() {
    return Doctrine_Core::getTable('MailingListMetaChoice');
  }

  public function getByMetaIdCached($id, $target_list_id = null) {
    if ($target_list_id) {
      $query = MailingListTable::getInstance()->createQuery('ml');

      if ($target_list_id)
        $query->where('ml.id = ?', $target_list_id); // help the db

      $result = $query->leftJoin('ml.MailingListMeta mlm')
        ->leftJoin('mlm.MailingListMetaChoice mlmc')
        ->leftJoin('mlmc.ContactMeta cm')
        ->andWhere('cm.mailing_list_meta_id = ?', $id)
        ->orderBy('mlmc.choice ASC')
        ->useResultCache()
        ->fetchOne();

      /* @var $result MailingList */

      if (!$result)
        return array();
      $a = $result->getMailingListMeta()->getFirst();
      if ($a)
        return $a->getMailingListMetaChoice();
      return array();
    }
  }

}
